import { CARD_ADD_ITEM, CARD_REMOVE_ITEM } from '../Constant/CartConstant';

export const cartReducer = (state = { cardItem: [] }, action) => {
     switch (action.type) {
          case CARD_ADD_ITEM:
               const items = action.payload;
               const existItem = state.cardItem.find((x) => x.product === items.product);
               if (existItem) {
                    return {
                         ...state,
                         cardItems: state.cardItem.map((x) => (x.product === existItem.product ? items : x))
                    };
               } else {
                    return {
                         ...state,
                         cardItem: [...state.cardItem, items]
                    };
               }

          default:
               return state;
     }
};
