import { Link } from 'react-router-dom';
import {Row,Col, Image, ListGroup,Card, ListGroupItem, Button} from 'react-bootstrap';
import { useDispatch, useSelector } from 'react-redux';
import Rating from '../Component/Rating';
import React ,{useEffect} from 'react';
import {Product} from '../Actions/ProductAction'
import {Loader } from '../Component/Loader'
import {Message} from '../Component/Message'


const ProductScreen = ({ match }) => {
     useEffect(() => {
    Dispatch(Product(match.params.id))  

     }, [Dispatch,match.params.Id]); 
     
     const Dispatch = useDispatch();     
    const state = useSelector(state => state.product)
    const {Loading,product,error}=state
   

  //const product={} 
     return (
          <>
          <Link className="btn btn-light my-3"  to="/" >Go Back</Link>
         {(Loading?<Loader/>:error?<Message/>:
         
          <Row><Col md={6}><Image src={product?.image} alt={product?.name} fluid/></Col><Col md={3}>
               <ListGroup variant='flush'>
                <ListGroup.Item>
                     <h3>{product?.name}</h3>
                </ListGroup.Item>
                <ListGroup.Item>
                <Rating value={product?.rating} text={`${product?.numReviews} reviews`}></Rating>

                </ListGroup.Item>
                <ListGroup.Item>
                     price:{product?.price}
                </ListGroup.Item>
                <ListGroup.Item>
                     description:{product?.description}
                </ListGroup.Item>

               </ListGroup>
               </Col>
               <Col md={3}>
                <Card>
               <ListGroup variant='flush'>
                  <ListGroupItem><Row><Col> Price</Col><Col>${product?.price}</Col></Row></ListGroupItem>
                  <ListGroupItem><Row><Col>Stack</Col><Col>{product?.countInStock?'in Stock':'Out Of  Stock'}</Col></Row></ListGroupItem>
                  <ListGroupItem><Button className='btn-block' type='button' disabled={product?.countInStock===0}>Add to Chart</Button></ListGroupItem>
               </ListGroup>
               
                </Card>
               </Col>
               
               </Row>)}
          </>
     )
}

export default ProductScreen
